ValidationRule.errorConditionFormula:
OR(AND(ISPICKVAL(Opportunity.StageName,"P. Close"), 
       ISPICKVAL(Opportunity.ApprovalStatus__c,"Approved")
   ), 
   AND(
       PRIORVALUE(IsOpen__c) = false, 
       Opportunity.OpportunityRecordTypeName__c = "Zorg",  
       OR(
          ISPICKVAL(Opportunity.StageName,"Closed Won"), 
          ISPICKVAL(Opportunity.StageName,"Closed Lost") 
       )
   )
)
